/*global google*/
import React from 'react'
import "./map.css";
import { MDBtn, MBtn } from './Button'
import { GoogleMap, Marker, InfoWindow, DirectionsRenderer } from "@react-google-maps/api";
import usePlacesAutocomplete, { getGeocode, getLatLng } from "use-places-autocomplete";
import { Combobox, ComboboxInput, ComboboxPopover, ComboboxList, ComboboxOption } from "@reach/combobox";
import "@reach/combobox/styles.css";
import { LocateMDBtn, LocateMBtn } from './LocateButton'



const mapContainerStyle = {
  height: "80vh",
  width: "50%",
};

const center = {
  lat: 38.660783,
  lng: -9.205681,
};


function CreateEvents() {



  const [state, setState] = React.useState({
    username: '',
    date: '',
    time: '',
    origin: '',
    description: '',
    directions: null
  })


  function handleChange(evt) {
    const value = evt.target.value;
    setState({
      ...state,
      [evt.target.name]: value
    });
  }



  const handleContinue = e => {
    e.preventDefault();
    console.log("entrei no continue")

    const request = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Accept': 'application/json'
      },
      body: JSON.stringify({
        username: state.username,
        origin: state.origin,
        time: state.time

      })
    };
    fetch('https://alpha-312722.appspot.com/rest/CriarRota', request)
      .then(async response => {

        if (response.ok) {
          console.log("Event successfully created!")
        }
        else {
          console.log("Something went wrong.")

        }
      })
  };




  const [markers, setMarkers] = React.useState([]);
  const [selected, setSelected] = React.useState([]);
  var service = new google.maps.DirectionsService();



  const onMapClick = React.useCallback((e) => {
    setMarkers((current) => [
      ...current,
      {
        lat: e.latLng.lat(),
        lng: e.latLng.lng(),

      },
    ]);
  }, []);

  const onMarkerClick = React.useCallback((e) => {
    setSelected((current) => [
      ...current, {

        lat: e.latLng.lat(),
        lng: e.latLng.lng(),
        active: true,
      }
    ])


  },[])


  const mapRef = React.useRef();
  const onMapLoad = React.useCallback((map) => {
    mapRef.current = map;
  }, []);

  const panTo = React.useCallback(({ lat, lng }) => {
    mapRef.current.panTo({ lat, lng });
    mapRef.current.setZoom(20);
  }, []);

  const {
    ready,
    value,
    suggestions: { status, data },
    setValue,
    clearSuggestions,
  } = usePlacesAutocomplete({
    requestOptions: {
      location: { lat: () => 38.660783, lng: () => -9.205681 },
      radius: 100 * 1000,
    },
  });

  const handleInput = (e) => {
    setValue(e.target.value);
  };

  const handleSelect = async (address) => {
    setValue(address, false);
    clearSuggestions();

    try {
      const results = await getGeocode({ address });
      console.log("results" + results[0]);
      const { lat, lng } = await getLatLng(results[0]);
      panTo({ lat, lng });
      setMarkers((current) => [
        ...current,
        {
          lat: lat,
          lng: lng,

        },
      ]);
      const waypoints = markers.map(p => ({
        location: { lat: p.lat, lng: p.lng },
        stopover: true
      }))


      if (waypoints.length >= 2) {
        const origin = waypoints[0].location;
        const destination = waypoints.pop().location;
        console.log(origin)

        service.route(

          {
            origin: origin,
            destination: destination,
            travelMode: google.maps.TravelMode.WALKING,
            waypoints: waypoints
          },
          (result, status) => {
            if (status === google.maps.DirectionsStatus.OK) {
              setState({
                directions: result
              }); 
            } else {
              console.error(`error fetching directions ${result}`);
            }
          }

        )
      }

    } catch (error) {
      console.log("Error: ", error);
    }
  };

  const onMarkerDragEnd = (e, index) => {
    console.log(index);
    markers[index] = { ...markers[index], lat: e.latLng.lat(), lng: e.latLng.lng() }

    const waypoints = markers.map(p => ({
      location: { lat: p.lat, lng: p.lng },
      stopover: true
    }))

    console.log("waypoints " + waypoints.length)
    if (waypoints.length >= 2) {
      const origin = waypoints[0].location;
      const destination = waypoints.pop().location;
      service.route(
        {
          origin: origin,
          destination: destination,
          travelMode: google.maps.TravelMode.WALKING,
          waypoints: waypoints
        },
        (result, status) => {
          if (status === google.maps.DirectionsStatus.OK) {

            setState({
              directions: result
            });
          } else {
            console.error(`error fetching directions ${result}`);
          }
        }

      )
    }



  };
  /* const onMarkerClick = (e,index) => { 
     console.log(index);
     markers[index] = {...markers[index],lat: e.latLng.lat(), lng:e.latLng.lng(),active:true}
     console.log(markers[index].active)
   };*/
  const onInfoClose = (e, index) => {
    selected[index] = { ...selected[index], lat: selected[index].lat, lng: selected[index].lng, active: false }
  }




  //const origin = markers[0];
  //const destination = markers[markers.length-1];


  const waypoints = markers.map(p => ({
    location: { lat: p.lat, lng: p.lng },
    stopover: true
  }))

  console.log("waypoints " + waypoints.length)
  if (waypoints.length >= 2) {
    const origin = waypoints[0].location;
    const destination = waypoints.pop().location;
    console.log(origin)
    service.route(

      {
        origin: origin,
        destination: destination,
        travelMode: google.maps.TravelMode.WALKING,
        waypoints: waypoints
      },
      (result, status) => {
        if (status === google.maps.DirectionsStatus.OK) {

          setState({
            directions: result
          });
        } else {
          console.error(`error fetching directions ${result}`);
        }
      }

    )
  }
/*
  for (var i = 0; i < markers.length; i++) {
    if ((i + 1) < markers.length) {
        var src = markers[i];
        var des = markers[i + 1];
        service.route({
            origin: src,
            destination: des,
            travelMode: google.maps.DirectionsTravelMode.WALKING
        }, function(result, status) {
            if (status == google.maps.DirectionsStatus.OK) {
                //Initialize the Path Array
                var path = new google.maps.MVCArray();
                var poly = new google.maps.Polyline();

                poly.setPath(path);

                for (var i = 0, len = result.routes[0].overview_path.length; i < len; i++) {
                    path.push(result.routes[0].overview_path[i]);
                }
            }
        });

    }
} 

