import React, { Component } from 'react';
import Category from './categories';

import './FormP.css';

const formValid = ({ formErrors, ...rest }) => {
  let valid = true;

  Object.values(formErrors).forEach(val => val.length > 0 && (valid = false));

  if (rest.username.length === 0 || rest.email.length === 0 || rest.password.length === 0 || rest.confirmPassword.length === 0) {
    valid = false;
  }
  return valid;
}

export class FormProfile extends Component {

  continue = e => {
    e.preventDefault();
  
    const request = {
      method: 'POST',

      headers: {
        'Content-Type': 'application/json',
        'Accept': 'application/json',
        'Authorization': 'Bearer ' + localStorage.getItem("user") 

      },
      body: JSON.stringify({

        userId: this.props.values.email,
        username: this.props.values.username,
        password: this.props.values.password,
        confirmPassword: this.props.values.confirmPassword,
        address: this.props.values.address,
        zipcode: this.props.values.zipcode,
        dateofbirth: this.props.values.dateofbirth,
        gender: this.props.values.gender,
        phone: this.props.values.phone,
      })
    };
    fetch('https://apdc-fasebeta.appspot.com/update/profile', request)
      .then(async response => {

        if (response.ok) {
          if (formValid(this.props.values)) {
            console.log("Changes saved")
          }
          console.log("correu bem")
        }
        else {
          console.log("Something went wrong")
        }
      })
  };

  back = e => {
    e.preventDefault();
    this.props.prevStep();
  };
  

  render() {
    const { values, handleChange } = this.props;

    return (
      <div className='profile-container1'>
        <form className='profile' onSubmit={this.continue}>
          <div className='grid-container'>
            <div className='profile-inputs'>
              <label className='profile-label'>Volunteer</label>
              <input
                className='profile-input'
                type='text'
                name='username'
                placeholder='Volunteer'
                onChange={handleChange('username')}
                defaultValue={values.username}
              />
              {values.formErrors.username.length > 0 && (<p className='p'>{values.formErrors.username}</p>)}
            </div>
            <div className='profile-inputs'>
              <label className='profile-label'>Email</label>
              <input
                className='profile-input'
                type='email'
                name='email'
                placeholder='Email'
                onChange={handleChange('email')}
                defaultValue={values.email}
              />
              {this.props.values.formErrors.email.length > 0 && (<p className='p'>{this.props.values.formErrors.email}</p>)}
            </div></div>
          <div className='grid-container'>
            <div className='profile-inputs'>
              <label className='profile-label'>Password</label>
              <input
                className='form-input'
                type='password'
                name='password'
                placeholder='Password'
                onChange={handleChange('password')}

              /></div>
            <div className='profile-inputs'>
              <label className='profile-label'>Confirm Password</label>
              <input
                className='profile-input'
                type='password'
                name='confirmPassword'
                placeholder='Confirm your password'
                value={values.confirmPassword}
                onChange={handleChange('confirmPassword')}
              />
            </div>
          </div>
          <div className='grid-container'>
            <div className='profile-inputs'>
              <label className='profile-label'>Date of Birth</label>
              <input
                className='profile-input'
                type='date'
                name='dateofbirth'
                onChange={handleChange('dateofbirth')}
                defaultValue={values.phone}
              />
            </div>
            <div className='profile-inputs'>
              <label className='profile-label'>Gender</label>
              <input
                className='profile-input'
                type='text'
                name='gender'
                placeholder='Gender'
                onChange={handleChange('gender')}
                defaultValue={values.gender}
              />
            </div>
          </div>
          <div className='grid-container2'>
            <div className='profile-inputs'>
              <label className='profile-label'>Phone</label>
              <input
                className='profile-input'
                type='text'
                name='phone'
                placeholder='Phone Number'
                onChange={handleChange('phone')}
                defaultValue={values.phone}
              />
            </div>
            <div className='profile-inputs'>
              <label className='profile-label'>Address</label>
              <input
                className='profile-input'
                type='text'
                name='address'
                placeholder='Address'
                onChange={handleChange('address')}
                defaultValue={values.address}
              />
            </div>
            <div className='profile-inputs'>
              <label className='profile-label'>Zip-Code</label>
              <input
                className='profile-input'
                type='text'
                name='zipcode'
                placeholder='0000-000'
                onChange={handleChange('zipcode')}
                defaultValue={values.address}
              />
              </div>
            </div>
            <Category/>
          <button className='save-btn' label='Save' type='submit'>Save</button>
        </form>
      </div>


    );
  }
};

export default FormProfile;
