import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function ListEvents() {

    const url = 'https://apdc-fasebeta.appspot.com/rest/events/list'

    const [filteredEvents, setFilteredEvents] = useState([])
    const [search, setSearch] = useState('')
    const [events, setEvents] = useState({
        loading: false,
        data: null,
        error: false
    })

    useEffect(() => {
        setEvents({
            loading: true,
            data: null,
            error: false
        })

        axios.get(url)
            .then(response => {
                setEvents({
                    loading: false,
                    data: response.data,
                    error: false
                })
            })
            .catch(() => {
                setEvents({
                    loading: false,
                    data: null,
                    error: true
                })
            })
    }, [url])

    let content = null

    if (events.error) {
        console.log("There was an error, please refresh or try again later.");
        content = <p>
            There was an error, please refresh or try again later.
        </p>
    }
    /*
        if (events.loading) {
            content = <Loader></Loader>
        }
    */


    if (events.data) {
        content =
            events.data.map((event, key) =>
                <div >
                    {event.name}
                </div>
            )
    }

    /*
    useEffect(() => {
        setFilteredEvents(
            events.filter(event => {
                event.name.toLowerCase().includes(search.toLowerCase())
            })        )
    }, [search, events])

*/
    return (
        <div>
            <div>
                <input type="text" placeholder="Search" onChange={e => setSearch(e.target.value)} />
            </div>
            {content}
        </div>
    )
}
